///  Original source code: https://vmf.geo.tuwien.ac.at/codes/
///
///      !!! This is the version with height correction !!!
///      !!! It has to be used with the grid !!!
/// 
///  (c) Department of Geodesy and Geoinformation, Vienna University of
///  Technology, 2016
/// 
///  This subroutine determines the VMF3 hydrostatic and wet mapping factors.
///  The a coefficients have to be inserted from discrete data, while the b
///  and c coefficients are of empirical nature containing a geographical
///  and temporal dependence, represented in spherical harmonics. The
///  spherical harmonics coefficients are developed to degree and order 12 and
///  are based on a 5x5 grid containing ray-tracing data from 2001-2010.
///  All input quantities have to be scalars!
/// 
///  INPUT:
///         o ah: hydrostatic mf coefficient a 
///          (http://vmf.geo.tuwien.ac.at/trop_products/)
///         o aw: wet mf coefficient a 
///          (http://vmf.geo.tuwien.ac.at/trop_products/)
///         o mjd: modified Julian date
///         o lat: latitude (radians)
///         o lon: longitude (radians)
///         o h_ell: ellipsoidal height (m)
///         o zd: zenith distance (radians)
/// 
///  OUTPUT:
///         o mfh: hydrostatic mapping factor
///         o mfw: wet mapping factor
/// 
/// --------------------------------------------------------------------------
/// 
///  m-file created by Daniel Landskron
/// 
/// --------------------------------------------------------------------------
///  History:
/// 
///  2016-03-14: m-file created
int dso::vmf3_ht(double ah, double aw, const dso::datetime<dso::nanoseconds> &t,
              double lat, double lon, double h_ell, double zd, double &mfh,
              double &mfw) noexcept {

#ifdef USE_EXTERNAL_CONSTS
  constexpr double pi(iers2010::DPI);
#else
  constexpr double pi(M_PI);
#endif

  // get fractional doy
  dso::ydoy_date ydoy(t.as_ydoy());
  int idoy = ydoy.__doy.as_underlying_type();
  double mjd;
  double fdoy = static_cast<double>(idoy) + std::modf(t.as_mjd(), &mjd);

  // conversions [rad]
  const double el = pi / 2e0 - zd;
  const double polDist = pi / 2e0 - lat;
  
  // a.) calculate Legendre polynomials

  // degree and order
  constexpr int nmax = 12;
  constexpr int size = nmax + 2;

  // unit vector
  const double x = std::sin(polDist) * std::cos(lon);
  const double y = std::sin(polDist) * std::sin(lon);
  const double z = std::cos(polDist);

  // Legendre polynomials
  double V[size][size];
  double W[size][size];
  V[1][1] = 1e0;
  W[1][1] = 0e0;
  V[2][1] = z * V[1][1];
  W[2][1] = 0e0;

  for (int n = 2; n <= nmax; n++) {
    V[n + 1][1] = ((2e0 * n - 1e0) * z * V[n][1] - (n - 1e0) * V[n - 1][1]) / (double)n;
    W[n + 1][1] = 0e0;
#ifdef DEBUG
    assert(n + 1 < size);
#endif
  }

  for (int m = 1; m <= nmax; m++) {
    int n;
    V[m + 1][m + 1] = (2e0 * m - 1e0) * (x * V[m][m] - y * W[m][m]);
    W[m + 1][m + 1] = (2e0 * m - 1e0) * (x * W[m][m] + y * V[m][m]);
#ifdef DEBUG
    assert(m + 1 < size);
#endif
    if (m < nmax) {
      V[m + 2][m + 1] = (2e0 * m + 1e0) * z * V[m + 1][m + 1];
      W[m + 2][m + 1] = (2e0 * m + 1e0) * z * W[m + 1][m + 1];
#ifdef DEBUG
      assert(m + 2 < size);
#endif
    }
    for (n = m + 2; n <= nmax; n++) {
      V[n + 1][m + 1] =
          ((2 * n - 1) * z * V[n][m + 1] - (n + m - 1) * V[n - 1][m + 1]) /
          (n - m);
      W[n + 1][m + 1] =
          ((2 * n - 1) * z * W[n][m + 1] - (n + m - 1) * W[n - 1][m + 1]) /
          (n - m);
#ifdef DEBUG
      assert((n + 1 < size) && (m + 1 < size));
#endif
    }
  }

  // b.) determine the coefficients bh, bw, ch and cw
  double bh_A0 = 0e0;
  double bh_A1 = 0e0;
  double bh_B1 = 0e0;
  double bh_A2 = 0e0;
  double bh_B2 = 0e0;
  double bw_A0 = 0e0;
  double bw_A1 = 0e0;
  double bw_B1 = 0e0;
  double bw_A2 = 0e0;
  double bw_B2 = 0e0;
  double ch_A0 = 0e0;
  double ch_A1 = 0e0;
  double ch_B1 = 0e0;
  double ch_A2 = 0e0;
  double ch_B2 = 0e0;
  double cw_A0 = 0e0;
  double cw_A1 = 0e0;
  double cw_B1 = 0e0;
  double cw_A2 = 0e0;
  double cw_B2 = 0e0;
  
  int i = 0;
  for (int n = 0; n <= nmax; n++) {
    for (int m = 0; m <= n; m++) {
      bh_A0 = bh_A0 +
              (anm_bh[0][i] * V[n + 1][m + 1] + bnm_bh[0][i] * W[n + 1][m + 1]);
      bh_A1 = bh_A1 +
              (anm_bh[1][i] * V[n + 1][m + 1] + bnm_bh[1][i] * W[n + 1][m + 1]);
      bh_B1 = bh_B1 +
              (anm_bh[2][i] * V[n + 1][m + 1] + bnm_bh[2][i] * W[n + 1][m + 1]);
      bh_A2 = bh_A2 +
              (anm_bh[3][i] * V[n + 1][m + 1] + bnm_bh[3][i] * W[n + 1][m + 1]);
      bh_B2 = bh_B2 +
              (anm_bh[4][i] * V[n + 1][m + 1] + bnm_bh[4][i] * W[n + 1][m + 1]);
      
      ++i;
    }
  }

  i = 0;
  for (int n = 0; n <= nmax; n++) {
    for (int m = 0; m <= n; m++) {
      bw_A0 = bw_A0 +
              (anm_bw[0][i] * V[n + 1][m + 1] + bnm_bw[0][i] * W[n + 1][m + 1]);
      bw_A1 = bw_A1 +
              (anm_bw[1][i] * V[n + 1][m + 1] + bnm_bw[1][i] * W[n + 1][m + 1]);
      bw_B1 = bw_B1 +
              (anm_bw[2][i] * V[n + 1][m + 1] + bnm_bw[2][i] * W[n + 1][m + 1]);
      bw_A2 = bw_A2 +
              (anm_bw[3][i] * V[n + 1][m + 1] + bnm_bw[3][i] * W[n + 1][m + 1]);
      bw_B2 = bw_B2 +
              (anm_bw[4][i] * V[n + 1][m + 1] + bnm_bw[4][i] * W[n + 1][m + 1]);
      ++i;
    }
  }

  i = 0;
  for (int n = 0; n <= nmax; n++) {
    for (int m = 0; m <= n; m++) {
      ch_A0 = ch_A0 +
              (anm_ch[0][i] * V[n + 1][m + 1] + bnm_ch[0][i] * W[n + 1][m + 1]);
      ch_A1 = ch_A1 +
              (anm_ch[1][i] * V[n + 1][m + 1] + bnm_ch[1][i] * W[n + 1][m + 1]);
      ch_B1 = ch_B1 +
              (anm_ch[2][i] * V[n + 1][m + 1] + bnm_ch[2][i] * W[n + 1][m + 1]);
      ch_A2 = ch_A2 +
              (anm_ch[3][i] * V[n + 1][m + 1] + bnm_ch[3][i] * W[n + 1][m + 1]);
      ch_B2 = ch_B2 +
              (anm_ch[4][i] * V[n + 1][m + 1] + bnm_ch[4][i] * W[n + 1][m + 1]);
      ++i;
    }
  }

  i = 0;
  for (int n = 0; n <= nmax; n++) {
    for (int m = 0; m <= n; m++) {
      cw_A0 = cw_A0 +
              (anm_cw[0][i] * V[n + 1][m + 1] + bnm_cw[0][i] * W[n + 1][m + 1]);
      cw_A1 = cw_A1 +
              (anm_cw[1][i] * V[n + 1][m + 1] + bnm_cw[1][i] * W[n + 1][m + 1]);
      cw_B1 = cw_B1 +
              (anm_cw[2][i] * V[n + 1][m + 1] + bnm_cw[2][i] * W[n + 1][m + 1]);
      cw_A2 = cw_A2 +
              (anm_cw[3][i] * V[n + 1][m + 1] + bnm_cw[3][i] * W[n + 1][m + 1]);
      cw_B2 = cw_B2 +
              (anm_cw[4][i] * V[n + 1][m + 1] + bnm_cw[4][i] * W[n + 1][m + 1]);
      ++i;
    }
  }

  // adding the seasonal amplitudes for the specified doy to the mean values
  const double bh = bh_A0 + bh_A1 * std::cos(fdoy / 365.25e0 * 2 * pi) +
                    bh_B1 * std::sin(fdoy / 365.25e0 * 2 * pi) +
                    bh_A2 * std::cos(fdoy / 365.25e0 * 4 * pi) +
                    bh_B2 * std::sin(fdoy / 365.25e0 * 4 * pi);
  const double bw = bw_A0 + bw_A1 * std::cos(fdoy / 365.25e0 * 2 * pi) +
                    bw_B1 * std::sin(fdoy / 365.25e0 * 2 * pi) +
                    bw_A2 * std::cos(fdoy / 365.25e0 * 4 * pi) +
                    bw_B2 * std::sin(fdoy / 365.25e0 * 4 * pi);
  const double ch = ch_A0 + ch_A1 * std::cos(fdoy / 365.25e0 * 2 * pi) +
                    ch_B1 * std::sin(fdoy / 365.25e0 * 2 * pi) +
                    ch_A2 * std::cos(fdoy / 365.25e0 * 4 * pi) +
                    ch_B2 * std::sin(fdoy / 365.25e0 * 4 * pi);
  const double cw = cw_A0 + cw_A1 * std::cos(fdoy / 365.25e0 * 2 * pi) +
                    cw_B1 * std::sin(fdoy / 365.25e0 * 2 * pi) +
                    cw_A2 * std::cos(fdoy / 365.25e0 * 4 * pi) +
                    cw_B2 * std::sin(fdoy / 365.25e0 * 4 * pi);

  // calculating the hydrostatic and wet mapping factors
  mfh = (1e0 + (ah / (1e0 + bh / (1e0 + ch)))) /
        (std::sin(el) + (ah / (std::sin(el) + bh / (std::sin(el) + ch))));
  mfw = (1e0 + (aw / (1e0 + bw / (1e0 + cw)))) /
        (std::sin(el) + (aw / (std::sin(el) + bw / (std::sin(el) + cw))));

  // height correction for the hydrostatic part [Niell, 1996]
  const double a_ht = 2.53e-5;
  const double b_ht = 5.49e-3;
  const double c_ht = 1.14e-3;
  const double h_ell_km = h_ell / 1000e0; // convert height to km
  const double ht_corr_coef =
      1e0 / std::sin(el) -
      (1e0 + (a_ht / (1e0 + b_ht / (1e0 + c_ht)))) /
          (std::sin(el) +
           (a_ht / (std::sin(el) + b_ht / (std::sin(el) + c_ht))));
  const double ht_corr = ht_corr_coef * h_ell_km;
  mfh += ht_corr;

  return 0;
}
